//get date string for today's date (e.g. '2011-01-01')
function datestring () {
  var d = new Date(Date.now() - 24*60*60*1000);  //est timezone
  return d.getUTCFullYear()   + '-'
     +  (d.getUTCMonth() + 1) + '-'
     +   d.getDate();
};
